import React, { Component } from 'react';
import './App.css';
// import GameStart from './components/GameStart';
import API from './utils/API';
import axios from "axios";



class App extends Component {
  state = {
    cards: [],
    query: "",
    nutrition: {},
    name: "",
    keto : {
      protein: {
        grams: 0,
        calories: 0
      },
      carbs: {
        grams: 0,
        calories: 0
      },
      fiber: {
        grams: 0,
        calories: 0
      },
      netCarbs : {
        grams: 0,
        calories: 0
      },
      fat: {
        grams: 0,
        calories: 0
      },
    },
    total: 0,
    ratios: {
      protein: 0,
      carbs: 0,
      fat: 0
    }
  };
  submitCheck = event => {
    if(event.key === "Enter"){
        this.handleFormSubmit(event);
    }
  }
  handleCalc = () => {
    const nutrition = this.state.keto;
    const protein = nutrition.protein.calories;
    const fat = nutrition.fat.calories;
    const carbs = nutrition.netCarbs.calories;
    const total = fat + carbs + protein;
    const ratios = {
      fat: fat/total,
      carbs: carbs/total,
      protein: protein/total
    }
    this.setState({results: ratios});
    console.log(ratios);


  }
  handleInputChange = event => {
    this.setState({query : event.target.value}, res => {
    console.log(this.state.query);
      API.getResults(this.state.query).then(res => {
        if(res.data.hits.length > 0){
          console.log(res.data.hits[0].fields.item_name);
        } else {
          console.log("nope");
        }
      });
    });
    if(event.keyCode === "a"){
        // this.handleFormSubmit(event)
    }
}
loadNutrition = (query) => {
  API.getNutrition(query).then( res => {
    console.log(res.data);
    this.setState({
      name: res.data.item_name,
      nutrition: res.data,
      keto: {
        fat: {
          grams: res.data.nf_total_fat,
          calories: res.data.nf_total_fat*9
        },
        carbs: {
          grams: res.data.nf_total_carbohydrate,
          calories: res.data.nf_total_carbohydrate*4
        },
        fiber: {
          grams: res.data.nf_dietary_fiber,
          calories: res.data.nf_dietary_fiber*4
        },
        netCarbs : {
          grams: res.data.nf_total_carbohydrate - res.data.nf_dietary_fiber,
          calories: res.data.nf_total_carbohydrate*4 - res.data.nf_dietary_fiber*4
        },
        protein: {
          grams: res.data.nf_protein,
          calories: res.data.nf_protein*4
        }
      }
    });

  }).then( res => {
    this.handleCalc();
  });
}
handleFormSubmit = event => {
  let query;
  if(this.state.query){
    query = this.state.query;
  } else {
    query = "eggs"
  }
  this.loadNutrition(query);

}

componentDidMount(){
  this.loadNutrition("eggs");

  // this.setState({nutrition : API.getNutrition("eggs")}).then( res => {
  //   console.log(this.state.nutrition);
  // });
}


  render() {
    return (
      <div className="content-wrap">
        {/* <GameStart/> */}

        <div className="header-center">
                        <input className="search-query" placeholder="search food" onKeyPress={this.submitCheck} onChange={this.handleInputChange}/>
                        <button className="formSubmit" onClick={this.handleFormSubmit}>Search</button>
                        <h1>{this.state.name}</h1>
        </div>
        <table>
          <tbody>
          <tr className="fat">
              <td className="grams">Fat: {this.state.keto.fat.grams} grams</td>
              <td>{this.state.keto.fat.calories} calories</td>
            </tr>
            <tr>
              <td className="grams">Total Carbs: {this.state.keto.carbs.grams} grams</td>
              <td>{this.state.keto.carbs.calories} calories</td>
            </tr>
            <tr>
              <td className="grams">Fiber: {this.state.keto.fiber.grams} grams</td>
              <td>{this.state.keto.fiber.calories} calories</td>
            </tr>
            <tr>
              <td className="grams">Net Carbs: {this.state.keto.netCarbs.grams} grams</td>
              <td>{this.state.keto.netCarbs.calories} calories</td>
            </tr>
            <tr>
              <td className="grams">Protein: {this.state.keto.protein.grams} grams</td>
              <td>{this.state.keto.protein.calories} calories</td>
            </tr>
            <tr>
              <td>{this.state.total} </td>
            </tr>
            <tr>
              <td>

              </td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}

export default App;

/*
  2 options:
    - form that allows you to enter nutrition info
    - search field that permits you to search a nutrition facts API
