import React, { Component } from 'react';
import './App.css';
// import GameStart from './components/GameStart';
import API from './utils/API';
import axios from "axios";



class App extends Component {
  state = {
    cards: [],
    query: "",
    nutrition: {},
    keto : {
      protein: 0,
      carbs: 0,
      fat: 0
    }
  };

componentDidMount(){
  API.getNutrition("eggs").then( res => {
    console.log(res.data);
    this.setState({
      nutrition: res.data,
      keto: {
        fat: {
          grams: res.data.nf_total_fat,
          calories: res.data.nf_total_fat*9
        },
        carbs: {
          grams: res.data.nf_total_carbohydrate,
          calories: res.data.nf_total_carbohydrate*4
        },
        protein: {
          grams: res.data.nf_protein,
          calories: res.data.nf_protein*4
        }

      }
    });

  });

  // this.setState({nutrition : API.getNutrition("eggs")}).then( res => {
  //   console.log(this.state.nutrition);
  // });
}


  render() {
    return (
      <div className="content-wrap">
        {/* <GameStart/> */}
        <p className="fat">Fat: {this.state.keto.fat} = {this.state.nutrition.nf_total_fat*9} calories</p>
        <p className="nutrition-carbs">Carbs: {this.state.nutrition.nf_total_carbohydrate}</p>
        <p className="nutrition-protein">Protein: {this.state.nutrition.nf_protein}</p>
      </div>
    );
  }
}

export default App;

/*
  2 options:
    - form that allows you to enter nutrition info
    - search field that permits you to search a nutrition facts API
